'use strict';
import translations from './translations/translations';
const Hammer = require('hammerjs');
const Polyglot = require('node-polyglot');

function getElementCoordinate(position, size) {
    return position + (size / 2);
};

function calcAngleDegrees(x, y) {
    return Math.atan2(y, x) * 180 / Math.PI;
};

function preventSelection(event) {
    if (event.stopPropagation) event.stopPropagation();
    if (event.preventDefault) event.preventDefault();
    event.cancelBubble = true;
    event.returnValue = false;
};

function getRandomElementFromArray(array) {
    return array[getRandomIntFromRange(0, array.length - 1)];
};

function getRandomIntFromRange(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

function rotateElement(angleDegX, angleDegY, style) {
    style.transform = 'rotateY(' + angleDegX + 'deg) rotateX(' + angleDegY + 'deg)';
};

function getPageCoordinateOwner(event) {
    return event.type.indexOf('touch') > -1 ? event.touches[0] || event.changedTouches[0] : event;
};

function onDrag(moveEvent, element) {

    preventSelection(moveEvent);

    const coordinateOwner = moveEvent.srcEvent;

    const x = coordinateOwner.pageX,
        y = coordinateOwner.pageY;

    const elementOffset = element.getBoundingClientRect();

    const elementX = getElementCoordinate(elementOffset.x, elementOffset.width),
        elementY = getElementCoordinate(elementOffset.y, elementOffset.height);

    const angleDegX = calcAngleDegrees(elementX, x - elementX),
        angleDegY = calcAngleDegrees(elementY, y - elementY);

    rotateElement(angleDegX, angleDegY, element.style);
};

function locale(newLocale) {
    let storedLocale = newLocale;
    if (newLocale) {
        localStorage.setItem('locale', newLocale);
    } else {
        storedLocale = localStorage.getItem('locale');
    }

    let language = storedLocale;
    if (!storedLocale) {
        language = navigator.language || 'en';
        language = language.split('-').length > 0 ? language.split('-')[0] : language;
        localStorage.setItem('locale', language);
    }
    return language;
};

function renderTranslatedText(newLocale, savedRandomisationData) {
    const localeValue = locale(newLocale);

    const polyglot = new Polyglot({
        locale: localeValue,
        phrases: translations[localeValue],
    });

    const translationsKeys = Object.keys(translations[localeValue]);

    const randomisedText = {};

    translationsKeys.forEach((translationKey) => {
        const elementKey = translationKey;

        const translation = translations[localeValue][translationKey];

        if (!translation.length) {
            if (!savedRandomisationData) {
                const randomIndex = getRandomIntFromRange(0, Object.keys(translation).length - 1);
                randomisedText[translationKey] = randomIndex;
                translationKey = [translationKey, '.', randomIndex].join('');
            } else {
                translationKey = [translationKey, '.', savedRandomisationData[translationKey]].join('');
            }
        }

        const element = document.getElementById(elementKey);

        const translatedText = polyglot.t(translationKey);

        console.log(element.dataset);
        if (element.dataset.tooltip) {
            element.dataset.tooltip = translatedText;
        } else {
            element.innerText = translatedText;
        }
    });

    return randomisedText;
};

function orderLanguages(locale) {
    const languagesPicker = document.getElementById('language-picker');
    const languages = languagesPicker.children;

    let languageIndex = -1, i = 0;
    while (i < languages.length && languageIndex < 0) {
        if (languages[i].dataset.language === locale) {
            languageIndex = i;
        } else {
            i++;
        }
    }

    languagesPicker.insertBefore(languages[languageIndex], languages[0]);
};

function main() {
    const colors = ['darkslategrey', 'tomato', 'tan', 'steelblue', 'steal'];


    document.body.style.backgroundColor = getRandomElementFromArray(colors);

    const randomisedText = renderTranslatedText();

    orderLanguages(locale());


    const scene = document.getElementById('scene');
    const card = document.getElementById('card');
    const languages = document.getElementsByClassName('language');
    const languagesPicker = document.getElementById('language-picker');

    const sceneManager = new Hammer.Manager(scene),
        documentManager = new Hammer(document),
        cardManager = new Hammer(card);

    const DoubleTap = new Hammer.Tap({
        event: 'doubletap',
        taps: 2
    });

    sceneManager.add(DoubleTap);

    documentManager.on('panmove', (event) => {
        onDrag(event, scene);
    });
    documentManager.on('panend', () => {
        rotateElement(0, 0, scene.style);
    });

    sceneManager.on('doubletap', (doubleTapEvent) => {
        card.classList.toggle('is-flipped');
    });

    cardManager.on('swipe', (doubleTapEvent) => {
        card.classList.toggle('is-flipped');
    });

    for (let language of languages) {
        const languageManager = new Hammer(language);
        languageManager.on('tap', (tapEvent) => {
            if (languagesPicker.classList.contains('hover')) {
                renderTranslatedText(tapEvent.target.dataset.language, randomisedText);
                orderLanguages(tapEvent.target.dataset.language);
            }
            languagesPicker.classList.toggle('hover');
        });
    }
};

main();
